        private static void TestEncryptDecryptRaw(string data, string pub, string priv, string modulus, string cipher)
        {
            var rsa = new RSAEncryption();
            rsa.Parameters.Scheme = RSAEncryptionScheme.Raw;
            rsa.PublicKey = new RSAPublicKey(BitHelper.HexToBytes(modulus), BitHelper.HexToBytes(pub));
            byte[] cipherBytes = rsa.Encrypt(BitHelper.HexToBytes(data));

            Assert.IsTrue(ByteArray.Equals(cipherBytes, BitHelper.HexToBytes(cipher)));

            rsa = new RSAEncryption();
            rsa.Parameters.Scheme = RSAEncryptionScheme.Raw;
            rsa.PrivateKey = new RSAPrivateKey(BitHelper.HexToBytes(modulus), BitHelper.HexToBytes(priv));
            byte[] plainBytes = rsa.Decrypt(BitHelper.HexToBytes(cipher));

            Assert.IsTrue(ByteArray.Equals(plainBytes, BitHelper.HexToBytes(data)));
        }

        private static void TestEncryptDecryptPKCS1(string data, string pub, string priv, string modulus, string cipher)
        {
            var rsa = new RSAEncryption();
            rsa.PublicKey = new RSAPublicKey(BitHelper.HexToBytes(modulus), BitHelper.HexToBytes(pub));
            byte[] cipherBytes = rsa.Encrypt(BitHelper.HexToBytes(data));

            Assert.IsTrue(ByteArray.Equals(cipherBytes, BitHelper.HexToBytes(cipher)));

            rsa = new RSAEncryption();
            rsa.PrivateKey = new RSAPrivateKey(BitHelper.HexToBytes(modulus), BitHelper.HexToBytes(priv));
            byte[] plainBytes = rsa.Decrypt(BitHelper.HexToBytes(cipher));

            Assert.IsTrue(ByteArray.Equals(plainBytes, BitHelper.HexToBytes(data)));
        }
    }
